import React from 'react';
import { useNavigate } from 'react-router-dom';
import NavigationButtons from '../common/NavigationButtons';

const Scene2Personas = () => {
  const navigate = useNavigate();

  return (
    <div className="scene scene2">
      <div className="scene-content">
        <h2>Scene 2: Learner Personas</h2>
        <p>This scene is under development. Persona headshots needed.</p>
      </div>
      <NavigationButtons
        onPrevious={() => navigate('/scene1')}
        onNext={() => navigate('/scene3')}
      />
    </div>
  );
};

export default Scene2Personas;
